export const ALL_MODS: Mod[] = rawData.map(mod => ({
    name: mod.name || "Unknown Module",
    category: mod.category || "misc",
    description: mod.description || "SYSTEM_LOG // Entry ready for synchronization.",
    wiki: mod.wiki || mod.url || (mod.slug ? `https://modrinth.com/mod/${mod.slug}` : ''),
    url: mod.url,
    icon: mod.icon,
    slug: mod.slug,
    curseSlug: mod.curseSlug,
    isLibrary: mod.category === 'library'
})).sort((a, b) => a.name.localeCompare(b.name));